mapper.update:
update prod_bom_plm
set voucher_no = #{vocherNo}
where deleted = 'F'
  and bom_no = #{bomCode}
  and company_id = #{companyId}

update prod_bom_plm
set bom_status = #{bomStatus}
where deleted = 'F'
  and company_id = #{companyId}
  and bom_no = #{bomCode}

mapper.select:
select bom_status bomStatus
from prod_bom_plm
where deleted = 'F'
and company_id = #{companyId}
and bom_no = #{bomCode}

    AND versions = #{versions}

SELECT s.ID id, CONCAT(s.MATTERS_NO, '/', s.MATTERS_NAME) mattersInfo, s.MATTERS_NO mattersNo, s.NORMS allInfo, s.UNIT unit, s.WEIGHT weight, t.type_name typeName FROM MATTERS_STORE_IOS s LEFT JOIN matters_type_ios t ON s.TYPE_ID = t.id AND t.deleted = 'F' WHERE s.deleted = 'F' and s.is_midprod != 2 AND s.company_id = #{companyId}
AND (s.matters_no LIKE CONCAT('%', #{codeOrName,jdbcType=VARCHAR}, '%') OR s.matters_name LIKE CONCAT('%', #{codeOrName,jdbcType=VARCHAR}, '%'))
AND s.matters_no != #{parentNo} AND s.store_id is null ;

SELECT product_mid_plm.id, product_mid_plm.mid_prod_no midProdNo, product_mid_plm.mid_prod_name midProdName
FROM product_mid_plm
WHERE deleted = 'F'
  AND company_id = #{companyId}
  AND parent_no = #{parentNo}
  AND store_id is null

SELECT count(*) FROM prod_bom_plm INNER JOIN voucher_main_oa ON prod_bom_plm.voucher_no = voucher_main_oa.voucher_no AND voucher_main_oa.company_id = #{entity.companyId} AND voucher_main_oa.deleted = 'F' LEFT JOIN matters_store_ios ON prod_bom_plm.prod_no = matters_store_ios.matters_no AND matters_store_ios.company_id = #{entity.companyId} AND matters_store_ios.deleted = 'F' AND matters_store_ios.store_id is null LEFT JOIN product_store_plm ON prod_bom_plm.prod_no = product_store_plm.prod_no AND product_store_plm.company_id = #{entity.companyId} AND product_store_plm.deleted = 'F' AND product_store_plm.store_id is null WHERE prod_bom_plm.deleted = 'F' AND prod_bom_plm.company_id = #{entity.companyId} AND matters_store_ios.store_id is null AND product_store_plm.store_id is null
AND prod_bom_plm.voucher_no = #{entity.voucherNo,jdbcType=VARCHAR}
AND prod_bom_plm.bom_no = #{entity.bomNo,jdbcType=VARCHAR}
and prod_bom_plm.prod_no LIKE CONCAT('%', #{entity.mattersName,jdbcType=VARCHAR}, '%')

SELECT prod_bom_plm.id, prod_bom_plm.voucher_no voucherNo, prod_bom_plm.bom_no bomNo, prod_bom_plm.make_date makeDate, prod_bom_plm.make_emp makeEmp, voucher_main_oa.is_finished voucherStatus, matters_store_ios.matters_name mattersName, product_store_plm.prod_name productName FROM prod_bom_plm INNER JOIN voucher_main_oa ON prod_bom_plm.voucher_no = voucher_main_oa.voucher_no AND voucher_main_oa.company_id = #{entity.companyId} AND voucher_main_oa.deleted = 'F' LEFT JOIN matters_store_ios ON prod_bom_plm.prod_no = matters_store_ios.matters_no AND matters_store_ios.company_id = #{entity.companyId} AND matters_store_ios.deleted = 'F' AND matters_store_ios.store_id is null LEFT JOIN product_store_plm ON prod_bom_plm.prod_no = product_store_plm.prod_no AND product_store_plm.company_id = #{entity.companyId} AND product_store_plm.deleted = 'F' AND product_store_plm.store_id is null WHERE prod_bom_plm.deleted = 'F' AND prod_bom_plm.company_id = #{entity.companyId} AND matters_store_ios.store_id is null AND product_store_plm.store_id is null
AND prod_bom_plm.voucher_no = #{entity.voucherNo,jdbcType=VARCHAR}
AND prod_bom_plm.bom_no = #{entity.bomNo,jdbcType=VARCHAR}
and prod_bom_plm.prod_no LIKE CONCAT('%', #{entity.mattersName,jdbcType=VARCHAR}, '%')
order by prod_bom_plm.id desc

SELECT prod_bom_plm.id
FROM prod_bom_plm
WHERE deleted = 'F'
  AND company_id = #{companyId}
  AND bom_no = #{bomNo}
