mapper.select:
SELECT m.voucher_no voucherNo
FROM prod_crafwork_main_plm m
         LEFT JOIN product_mid_plm i ON i.prod_no = m.prod_no AND i.company_id = m.company_id AND i.deleted = 'F'
WHERE i.mid_prod_no = #{midProdNo,jdbcType=VARCHAR}
  AND m.deleted = 'F'
  AND m.company_id = #{companyId,jdbcType=BIGINT}

SELECT m.path_no pathNo, s.prod_no prodNo, i.mid_prod_no midProdNo, v.voucher_no voucherNo, v.is_finished isFinished, d.label label FROM product_store_plm s LEFT JOIN prod_crafwork_main_plm m ON s.prod_no = m.prod_no AND s.company_id = m.company_id AND m.deleted = 'F' LEFT JOIN product_mid_plm i ON i.prod_no = m.prod_no AND i.company_id = m.company_id AND i.deleted = 'F' LEFT JOIN voucher_main_oa v ON v.voucher_no = m.voucher_no AND v.company_id = m.company_id AND v.deleted = 'F' LEFT JOIN crm.dict_crm d ON d.`value` = v.is_finished WHERE s.company_id = #{companyId,jdbcType=BIGINT}
AND s.prod_no = #{prodNo,jdbcType=VARCHAR}
AND i.mid_prod_no = #{midProdNo,jdbcType=VARCHAR}
AND m.path_no = #{pathNo,jdbcType=VARCHAR}
AND s.deleted = 'F' GROUP BY m.path_no

mapper.update:
UPDATE prod_crafwork_main_plm


        status = #{status,jdbcType=VARCHAR},


WHERE PATH_NO = #{pathNo,jdbcType=VARCHAR}
AND COMPANY_ID = #{companyId,jdbcType=BIGINT}
AND DELETED = 'F'

mapper.select:
SELECT m.path_no pathNo, concat_ws("/",m.prod_no,s.prod_name) midProdMsg, concat_ws("/",m.prod_no,s.prod_name) prodMsg, m.prod_no prodNo, m.prod_no midProdNo FROM prod_crafwork_main_plm m LEFT JOIN product_store_plm s ON s.prod_no = m.prod_no AND s.company_id = m.company_id AND s.deleted = 'F' left join voucher_main_oa o on o.voucher_no = m.voucher_no and o.company_id = m.company_id and o.deleted = 'F' WHERE m.company_id = #{companyId,jdbcType=BIGINT} AND m.voucher_no is not null AND m.path_no NOT IN (SELECT m.path_no FROM crafwork_change_main m LEFT JOIN voucher_main_oa o ON o.voucher_no = m.voucher_no AND m.company_id = o.company_id AND o.deleted = 'F' WHERE o.is_finished = 'AS002' AND m.company_id = #{companyId,jdbcType=BIGINT} AND m.deleted = 'F') AND o.is_finished != 'AS003' AND m.`status` != 'false'
AND (s.PROD_NAME LIKE CONCAT ('%',#{prodMsg,jdbcType=VARCHAR},'%') or (m.PROD_NO LIKE CONCAT ('%',#{prodMsg,jdbcType=VARCHAR},'%')))
AND m.path_no = #{pathNo,jdbcType=VARCHAR}
AND m.PROD_NO = #{prodNo,jdbcType=VARCHAR}
AND m.deleted = 'F'

SELECT m.id id, concat_ws("/",m.prod_no,s.prod_name) prodMsg, concat_ws("/",m.prod_no,s.prod_name) midProdMsg, m.prod_no prodNo, m.prod_no midProdNo, m.versions versions, m.PATH_NO pathNo, m.voucher_no voucherNo, o.is_finished isFinished FROM prod_crafwork_main_plm m LEFT JOIN product_store_plm s ON s.prod_no = m.prod_no AND s.company_id = m.company_id AND s.deleted = 'F' left join voucher_main_oa o on o.voucher_no = m.voucher_no and o.company_id = m.company_id and o.deleted = 'F' WHERE m.company_id = #{companyId,jdbcType=BIGINT} AND m.deleted = 'F' AND m.voucher_no is not null AND o.is_finished != 'AS003'
AND m.path_no = #{pathNo,jdbcType=VARCHAR}
AND m.PROD_NO = #{prodNo,jdbcType=VARCHAR}
