mapper.update:
UPDATE CRAFWORK_PARAM_PLM
CRAFWORK_ID = #{crafworkId,jdbcType=BIGINT}, PARAM = #{param,jdbcType=VARCHAR}, DELETED = #{deleted,jdbcType=VARCHAR}, CREATE_TIME = #{createTime,jdbcType=TIMESTAMP}, CREATE_BY = #{createBy,jdbcType=VARCHAR}, COMPANY_ID = #{companyId,jdbcType=BIGINT}, MODIFY_TIME = #{modifyTime,jdbcType=TIMESTAMP}, MODIFY_BY = #{modifyBy,jdbcType=VARCHAR},
WHERE ID = #{id,jdbcType=INTEGER}

UPDATE CRAFWORK_PARAM_PLM
   SET DELETED     = #{deleted,jdbcType=VARCHAR},
       MODIFY_BY   = #{modifyBy,jdbcType=VARCHAR},
       MODIFY_TIME = #{modifyTime,jdbcType=TIMESTAMP}
 WHERE ID = #{id,jdbcType=INTEGER}

mapper.select:

FROM CRAFWORK_PARAM_PLM
WHERE ID = #{id,jdbcType=INTEGER}
AND DELETED = #{deleted,jdbcType=VARCHAR}


FROM CRAFWORK_PARAM_PLM


        AND ID = #{id,jdbcType=INTEGER}


        AND CRAFWORK_ID = #{crafworkId,jdbcType=BIGINT}


        AND PARAM = #{param,jdbcType=VARCHAR}


        AND DELETED = #{deleted,jdbcType=VARCHAR}


        AND COMPANY_ID = #{companyId,jdbcType=BIGINT}


FROM CRAFWORK_PARAM_PLM


        AND ID = #{id,jdbcType=INTEGER}


        AND CRAFWORK_ID = #{crafworkId,jdbcType=BIGINT}


        AND PARAM = #{param,jdbcType=VARCHAR}


        AND DELETED = #{deleted,jdbcType=VARCHAR}


        AND COMPANY_ID = #{companyId,jdbcType=BIGINT}

    WHERE DELETED = "F"
