mapper.update:
UPDATE aps.MACHINE_SET_MES
MAC_NO = #{macNo,jdbcType=VARCHAR}, MAC_NAME = #{macName,jdbcType=VARCHAR}, FACTORY_ID = #{factoryId,jdbcType=BIGINT}, ORG_ID = #{orgId,jdbcType=BIGINT}, MAC_TYPE = #{macType,jdbcType=VARCHAR}, CRAFWORK_ID = #{crafworkId,jdbcType=BIGINT}, LIABLE = #{liable,jdbcType=VARCHAR}, MIN_AMOUNT = #{minAmount,jdbcType=DECIMAL}, MIN_EMP = #{minEmp,jdbcType=INTEGER}, IS_INSPECTION = #{isInspection,jdbcType=VARCHAR}, BRAND = #{brand,jdbcType=VARCHAR}, SPEC = #{spec,jdbcType=TEXT}, MAC_STATUS = #{macStatus,jdbcType=VARCHAR}, IS_FINISH = #{isFinish,jdbcType=INTEGER}, BARCODE = #{barcode,jdbcType=VARCHAR}, QRCODE = #{qrcode,jdbcType=VARCHAR}, POSITION = #{position,jdbcType=VARCHAR}, SERIAL = #{serial,jdbcType=INTEGER}, BUILDDATE = #{builddate,jdbcType=TIMESTAMP}, LOSEDATE = #{losedate,jdbcType=TIMESTAMP}, COMPANY_ID = #{companyId,jdbcType=BIGINT}, DELETED = #{deleted,jdbcType=VARCHAR}, CREATE_TIME = #{createTime,jdbcType=TIMESTAMP}, CREATE_BY = #{createBy,jdbcType=VARCHAR}, MODIFY_TIME = #{modifyTime,jdbcType=TIMESTAMP}, MODIFY_BY = #{modifyBy,jdbcType=VARCHAR},
WHERE ID = #{id,jdbcType=INTEGER}

UPDATE aps.MACHINE_SET_MES
   SET DELETED     = #{deleted,jdbcType=VARCHAR},
       MODIFY_BY   = #{modifyBy,jdbcType=VARCHAR},
       MODIFY_TIME = #{modifyTime,jdbcType=TIMESTAMP}
 WHERE ID = #{id,jdbcType=INTEGER}

mapper.select:

 FROM aps.MACHINE_SET_MES
WHERE ID = #{id,jdbcType=INTEGER}
  AND DELETED = #{deleted,jdbcType=VARCHAR}


  FROM aps.MACHINE_SET_MES


		AND ID = #{id,jdbcType=INTEGER}


		AND MAC_NO = #{macNo,jdbcType=VARCHAR}


		AND MAC_NAME = #{macName,jdbcType=VARCHAR}


		AND FACTORY_ID = #{factoryId,jdbcType=BIGINT}


		AND ORG_ID = #{orgId,jdbcType=BIGINT}


		AND MAC_TYPE = #{macType,jdbcType=VARCHAR}


		AND CRAFWORK_ID = #{crafworkId,jdbcType=BIGINT}


		AND LIABLE = #{liable,jdbcType=VARCHAR}


		AND MIN_AMOUNT = #{minAmount,jdbcType=DECIMAL}


		AND MIN_EMP = #{minEmp,jdbcType=INTEGER}


		AND IS_INSPECTION = #{isInspection,jdbcType=VARCHAR}


		AND BRAND = #{brand,jdbcType=VARCHAR}


		AND SPEC = #{spec,jdbcType=TEXT}


		AND MAC_STATUS = #{macStatus,jdbcType=VARCHAR}


		AND IS_FINISH = #{isFinish,jdbcType=INTEGER}


		AND BARCODE = #{barcode,jdbcType=VARCHAR}


		AND QRCODE = #{qrcode,jdbcType=VARCHAR}


		AND POSITION = #{position,jdbcType=VARCHAR}


		AND SERIAL = #{serial,jdbcType=INTEGER}


		AND BUILDDATE = #{builddate,jdbcType=TIMESTAMP}


		AND LOSEDATE = #{losedate,jdbcType=TIMESTAMP}


		AND COMPANY_ID = #{companyId,jdbcType=BIGINT}


		AND DELETED = #{deleted,jdbcType=VARCHAR}


  FROM aps.MACHINE_SET_MES


		AND ID = #{id,jdbcType=INTEGER}


		AND MAC_NO = #{macNo,jdbcType=VARCHAR}


		AND MAC_NAME = #{macName,jdbcType=VARCHAR}


		AND FACTORY_ID = #{factoryId,jdbcType=BIGINT}


		AND ORG_ID = #{orgId,jdbcType=BIGINT}


		AND MAC_TYPE = #{macType,jdbcType=VARCHAR}


		AND CRAFWORK_ID = #{crafworkId,jdbcType=BIGINT}


		AND LIABLE = #{liable,jdbcType=VARCHAR}


		AND MIN_AMOUNT = #{minAmount,jdbcType=DECIMAL}


		AND MIN_EMP = #{minEmp,jdbcType=INTEGER}


		AND IS_INSPECTION = #{isInspection,jdbcType=VARCHAR}


		AND BRAND = #{brand,jdbcType=VARCHAR}


		AND SPEC = #{spec,jdbcType=TEXT}


		AND MAC_STATUS = #{macStatus,jdbcType=VARCHAR}


		AND IS_FINISH = #{isFinish,jdbcType=INTEGER}


		AND BARCODE = #{barcode,jdbcType=VARCHAR}


		AND QRCODE = #{qrcode,jdbcType=VARCHAR}


		AND POSITION = #{position,jdbcType=VARCHAR}


		AND SERIAL = #{serial,jdbcType=INTEGER}


		AND BUILDDATE = #{builddate,jdbcType=TIMESTAMP}


		AND LOSEDATE = #{losedate,jdbcType=TIMESTAMP}


		AND COMPANY_ID = #{companyId,jdbcType=BIGINT}


