mapper.update:
UPDATE MATTERS_STORE_IOS
MATTERS_NO = #{mattersNo,jdbcType=VARCHAR}, MATTERS_NAME = #{mattersName,jdbcType=VARCHAR}, TYPE_ID = #{typeId,jdbcType=BIGINT}, NORMS = #{norms,jdbcType=VARCHAR}, PARAM = #{param,jdbcType=VARCHAR}, MODEL_DESC = #{modelDesc,jdbcType=VARCHAR}, BRAND = #{brand,jdbcType=VARCHAR}, MATERIAL = #{material,jdbcType=VARCHAR}, LEVEL = #{level,jdbcType=VARCHAR}, UNIT = #{unit,jdbcType=VARCHAR}, FACTORY = #{factory,jdbcType=VARCHAR}, CUR_AMOUNT = #{curAmount,jdbcType=DECIMAL}, ADV_AMOUNT = #{advAmount,jdbcType=DECIMAL}, PUR_AMOUNT = #{purAmount,jdbcType=DECIMAL}, WEIGHT = #{weight,jdbcType=DECIMAL}, METTER_WEI = #{metterWei,jdbcType=INTEGER}, STORE_ID = #{storeId,jdbcType=BIGINT}, IOS_DESC = #{iosDesc,jdbcType=VARCHAR}, VERSIONS = #{versions,jdbcType=VARCHAR}, IS_MIDPROD = #{isMidprod,jdbcType=VARCHAR}, STATUS = #{status,jdbcType=VARCHAR}, SECURE_STOCK = #{secureStock,jdbcType=DECIMAL}, BEF_DATE = #{befDate,jdbcType=BIGINT}, BULIDDATE = #{buliddate,jdbcType=TIMESTAMP}, LOSEDATE = #{losedate,jdbcType=TIMESTAMP}, DELETED = #{deleted,jdbcType=VARCHAR}, CREATE_TIME = #{createTime,jdbcType=TIMESTAMP}, CREATE_BY = #{createBy,jdbcType=VARCHAR}, COMPANY_ID = #{companyId,jdbcType=BIGINT}, MODIFY_TIME = #{modifyTime,jdbcType=TIMESTAMP}, MODIFY_BY = #{modifyBy,jdbcType=VARCHAR},
WHERE ID = #{id,jdbcType=INTEGER}

UPDATE MATTERS_STORE_IOS
SET DELETED     = #{deleted,jdbcType=VARCHAR},
    MODIFY_BY   = #{modifyBy,jdbcType=VARCHAR},
    MODIFY_TIME = #{modifyTime,jdbcType=TIMESTAMP}
WHERE ID = #{id,jdbcType=INTEGER}

mapper.select:

FROM MATTERS_STORE_IOS
WHERE ID = #{id,jdbcType=INTEGER}
AND DELETED = #{deleted,jdbcType=VARCHAR}


FROM MATTERS_STORE_IOS


        AND ID = #{id,jdbcType=INTEGER}


