mapper.update:
UPDATE PROD_BOM_DETAIL_PLM
BOM_NO = #{bomNo,jdbcType=VARCHAR}, SERIAL = #{serial,jdbcType=BIGINT}, MATTERS_NO = #{mattersNo,jdbcType=VARCHAR}, PARENT_NO = #{parentNo,jdbcType=VARCHAR}, NORMS = #{norms,jdbcType=VARCHAR}, PARAM = #{param,jdbcType=VARCHAR}, MODEL_DESC = #{modelDesc,jdbcType=VARCHAR}, MAT_VERSIONS = #{matVersions,jdbcType=VARCHAR}, AMOUNT = #{amount,jdbcType=DECIMAL}, UNIT = #{unit,jdbcType=VARCHAR}, CRAFWORK_ID = #{crafworkId,jdbcType=BIGINT}, BRAND = #{brand,jdbcType=VARCHAR}, BACKUP_MATTER = #{backupMatter,jdbcType=VARCHAR}, SON_BOM_NO = #{sonBomNo,jdbcType=VARCHAR}, PLM_DESC = #{plmDesc,jdbcType=VARCHAR}, DELETED = #{deleted,jdbcType=VARCHAR}, CREATE_TIME = #{createTime,jdbcType=TIMESTAMP}, CREATE_BY = #{createBy,jdbcType=VARCHAR}, COMPANY_ID = #{companyId,jdbcType=BIGINT}, MODIFY_TIME = #{modifyTime,jdbcType=TIMESTAMP}, MODIFY_BY = #{modifyBy,jdbcType=VARCHAR},
WHERE ID = #{id,jdbcType=INTEGER}

UPDATE PROD_BOM_DETAIL_PLM
SET DELETED     = #{deleted,jdbcType=VARCHAR},
    MODIFY_BY   = #{modifyBy,jdbcType=VARCHAR},
    MODIFY_TIME = #{modifyTime,jdbcType=TIMESTAMP}
WHERE ID = #{id,jdbcType=INTEGER}

mapper.select:

FROM PROD_BOM_DETAIL_PLM
WHERE ID = #{id,jdbcType=INTEGER}
AND DELETED = #{deleted,jdbcType=VARCHAR}


FROM PROD_BOM_DETAIL_PLM


        AND ID = #{id,jdbcType=INTEGER}


        AND BOM_NO = #{bomNo,jdbcType=VARCHAR}


        AND SERIAL = #{serial,jdbcType=BIGINT}


        AND MATTERS_NO = #{mattersNo,jdbcType=VARCHAR}


        AND PARENT_NO = #{parentNo,jdbcType=VARCHAR}


        AND NORMS = #{norms,jdbcType=VARCHAR}


        AND PARAM = #{param,jdbcType=VARCHAR}


        AND MODEL_DESC = #{modelDesc,jdbcType=VARCHAR}


        AND MAT_VERSIONS = #{matVersions,jdbcType=VARCHAR}


        AND AMOUNT = #{amount,jdbcType=DECIMAL}


        AND UNIT = #{unit,jdbcType=VARCHAR}


        AND CRAFWORK_ID = #{crafworkId,jdbcType=BIGINT}


        AND BRAND = #{brand,jdbcType=VARCHAR}


        AND BACKUP_MATTER = #{backupMatter,jdbcType=VARCHAR}


        AND SON_BOM_NO = #{sonBomNo,jdbcType=VARCHAR}


        AND PLM_DESC = #{plmDesc,jdbcType=VARCHAR}


        AND DELETED = #{deleted,jdbcType=VARCHAR}


        AND COMPANY_ID = #{companyId,jdbcType=BIGINT}


FROM PROD_BOM_DETAIL_PLM


        AND ID = #{id,jdbcType=INTEGER}


        AND BOM_NO = #{bomNo,jdbcType=VARCHAR}


        AND SERIAL = #{serial,jdbcType=BIGINT}


        AND MATTERS_NO = #{mattersNo,jdbcType=VARCHAR}


        AND PARENT_NO = #{parentNo,jdbcType=VARCHAR}


        AND NORMS = #{norms,jdbcType=VARCHAR}


        AND PARAM = #{param,jdbcType=VARCHAR}


        AND MODEL_DESC = #{modelDesc,jdbcType=VARCHAR}


        AND MAT_VERSIONS = #{matVersions,jdbcType=VARCHAR}


        AND AMOUNT = #{amount,jdbcType=DECIMAL}


        AND UNIT = #{unit,jdbcType=VARCHAR}


        AND CRAFWORK_ID = #{crafworkId,jdbcType=BIGINT}


        AND BRAND = #{brand,jdbcType=VARCHAR}


        AND BACKUP_MATTER = #{backupMatter,jdbcType=VARCHAR}


        AND SON_BOM_NO = #{sonBomNo,jdbcType=VARCHAR}


        AND PLM_DESC = #{plmDesc,jdbcType=VARCHAR}


        AND DELETED = #{deleted,jdbcType=VARCHAR}


        AND COMPANY_ID = #{companyId,jdbcType=BIGINT}

