mapper.update:
UPDATE PROD_MAC_PLM
PROD_NO = #{prodNo,jdbcType=VARCHAR}, MID_PROD_NO = #{midProdNo,jdbcType=VARCHAR}, CRAFWORK_ID = #{crafworkId,jdbcType=BIGINT}, MAC_NO = #{macNo,jdbcType=VARCHAR}, PERIOD_TIME = #{periodTime,jdbcType=INTEGER}, BEFORE_TIME = #{beforeTime,jdbcType=INTEGER}, BEHIND_TIME = #{behindTime,jdbcType=INTEGER}, RUN_TOTAL = #{runTotal,jdbcType=INTEGER}, DELETED = #{deleted,jdbcType=VARCHAR}, CREATE_TIME = #{createTime,jdbcType=TIMESTAMP}, CREATE_BY = #{createBy,jdbcType=VARCHAR}, COMPANY_ID = #{companyId,jdbcType=BIGINT}, MODIFY_TIME = #{modifyTime,jdbcType=TIMESTAMP}, MODIFY_BY = #{modifyBy,jdbcType=VARCHAR},
WHERE ID = #{id,jdbcType=INTEGER}

UPDATE PROD_MAC_PLM
   SET DELETED     = #{deleted,jdbcType=VARCHAR},
       MODIFY_BY   = #{modifyBy,jdbcType=VARCHAR},
       MODIFY_TIME = #{modifyTime,jdbcType=TIMESTAMP}
 WHERE ID = #{id,jdbcType=INTEGER}

mapper.select:

FROM PROD_MAC_PLM
WHERE ID = #{id,jdbcType=INTEGER}
AND DELETED = #{deleted,jdbcType=VARCHAR}


FROM PROD_MAC_PLM


        AND ID = #{id,jdbcType=INTEGER}


        AND PROD_NO = #{prodNo,jdbcType=VARCHAR}


        AND MID_PROD_NO = #{midProdNo,jdbcType=VARCHAR}


        AND CRAFWORK_ID = #{crafworkId,jdbcType=BIGINT}


        AND MAC_NO = #{macNo,jdbcType=VARCHAR}


        AND PERIOD_TIME = #{periodTime,jdbcType=INTEGER}


        AND BEFORE_TIME = #{beforeTime,jdbcType=INTEGER}


        AND BEHIND_TIME = #{behindTime,jdbcType=INTEGER}


        AND RUN_TOTAL = #{runTotal,jdbcType=INTEGER}


        AND DELETED = #{deleted,jdbcType=VARCHAR}


        AND COMPANY_ID = #{companyId,jdbcType=BIGINT}


FROM PROD_MAC_PLM


        AND ID = #{id,jdbcType=INTEGER}


        AND PROD_NO = #{prodNo,jdbcType=VARCHAR}


        AND MID_PROD_NO = #{midProdNo,jdbcType=VARCHAR}


        AND CRAFWORK_ID = #{crafworkId,jdbcType=BIGINT}


        AND MAC_NO = #{macNo,jdbcType=VARCHAR}


        AND PERIOD_TIME = #{periodTime,jdbcType=INTEGER}


        AND BEFORE_TIME = #{beforeTime,jdbcType=INTEGER}


        AND BEHIND_TIME = #{behindTime,jdbcType=INTEGER}


        AND RUN_TOTAL = #{runTotal,jdbcType=INTEGER}


        AND DELETED = #{deleted,jdbcType=VARCHAR}


        AND COMPANY_ID = #{companyId,jdbcType=BIGINT}

    WHERE DELETED = "F"
