mapper.update:
UPDATE hcm.QUARTERS_HCM
QUARTERS_NO = #{quartersNo,jdbcType=VARCHAR}, QUARTERS_DESC = #{quartersDesc,jdbcType=VARCHAR}, FUN_DESC = #{funDesc,jdbcType=VARCHAR}, HIRES = #{hires,jdbcType=INTEGER}, QU_QUIRE = #{quQuire,jdbcType=VARCHAR}, EDUCATIONAL = #{educational,jdbcType=VARCHAR}, SKILL = #{skill,jdbcType=VARCHAR}, DELETED = #{deleted,jdbcType=VARCHAR}, COMPANY_ID = #{companyId,jdbcType=BIGINT}, CREATE_TIME = #{createTime,jdbcType=TIMESTAMP}, CREATE_BY = #{createBy,jdbcType=VARCHAR}, MODIFY_TIME = #{modifyTime,jdbcType=TIMESTAMP}, MODIFY_BY = #{modifyBy,jdbcType=VARCHAR},
WHERE ID = #{id,jdbcType=INTEGER}

UPDATE hcm.QUARTERS_HCM
   SET DELETED     = #{deleted,jdbcType=VARCHAR},
       MODIFY_BY   = #{modifyBy,jdbcType=VARCHAR},
       MODIFY_TIME = #{modifyTime,jdbcType=TIMESTAMP}
 WHERE ID = #{id,jdbcType=INTEGER}

mapper.select:

FROM hcm.QUARTERS_HCM
WHERE ID = #{id,jdbcType=INTEGER}
AND DELETED = #{deleted,jdbcType=VARCHAR}


FROM hcm.QUARTERS_HCM


        AND ID = #{id,jdbcType=INTEGER}


        AND QUARTERS_NO = #{quartersNo,jdbcType=VARCHAR}


        AND QUARTERS_DESC = #{quartersDesc,jdbcType=VARCHAR}


        AND FUN_DESC = #{funDesc,jdbcType=VARCHAR}


        AND HIRES = #{hires,jdbcType=INTEGER}


        AND QU_QUIRE = #{quQuire,jdbcType=VARCHAR}


        AND EDUCATIONAL = #{educational,jdbcType=VARCHAR}


        AND SKILL = #{skill,jdbcType=VARCHAR}


        AND DELETED = #{deleted,jdbcType=VARCHAR}


        AND COMPANY_ID = #{companyId,jdbcType=BIGINT}


FROM hcm.QUARTERS_HCM


        AND ID = #{id,jdbcType=INTEGER}


        AND QUARTERS_NO = #{quartersNo,jdbcType=VARCHAR}


        AND QUARTERS_DESC = #{quartersDesc,jdbcType=VARCHAR}


        AND FUN_DESC = #{funDesc,jdbcType=VARCHAR}


        AND HIRES = #{hires,jdbcType=INTEGER}


        AND QU_QUIRE = #{quQuire,jdbcType=VARCHAR}


        AND EDUCATIONAL = #{educational,jdbcType=VARCHAR}


        AND SKILL = #{skill,jdbcType=VARCHAR}


        AND DELETED = #{deleted,jdbcType=VARCHAR}


        AND COMPANY_ID = #{companyId,jdbcType=BIGINT}

    WHERE DELETED = "F"
