mapper.update:
UPDATE VOUCHER_MAIN_OA
VOUCHER_NO = #{voucherNo,jdbcType=VARCHAR}, RAISER = #{raiser,jdbcType=VARCHAR}, RAISE_DATE = #{raiseDate,jdbcType=TIMESTAMP}, HOPE_DATE = #{hopeDate,jdbcType=TIMESTAMP}, EVENT_DESC = #{eventDesc,jdbcType=VARCHAR}, VOUCHER_TYPE = #{voucherType,jdbcType=VARCHAR}, WKFLOW_ID = #{wkflowId,jdbcType=BIGINT}, CHECK_ADVICE = #{checkAdvice,jdbcType=VARCHAR}, CHECKS = #{checks,jdbcType=VARCHAR}, IS_FINISHED = #{isFinished,jdbcType=VARCHAR}, REMARK = #{remark,jdbcType=VARCHAR}, DELETED = #{deleted,jdbcType=VARCHAR}, COMPANY_ID = #{companyId,jdbcType=BIGINT}, CREATE_TIME = #{createTime,jdbcType=TIMESTAMP}, CREATE_BY = #{createBy,jdbcType=VARCHAR}, MODIFY_TIME = #{modifyTime,jdbcType=TIMESTAMP}, MODIFY_BY = #{modifyBy,jdbcType=VARCHAR},
WHERE ID = #{id,jdbcType=INTEGER}

UPDATE VOUCHER_MAIN_OA
   SET DELETED     = #{deleted,jdbcType=VARCHAR},
       MODIFY_BY   = #{modifyBy,jdbcType=VARCHAR},
       MODIFY_TIME = #{modifyTime,jdbcType=TIMESTAMP}
 WHERE ID = #{id,jdbcType=INTEGER}

mapper.select:

FROM VOUCHER_MAIN_OA
WHERE ID = #{id,jdbcType=INTEGER}
AND DELETED = #{deleted,jdbcType=VARCHAR}


